import React, {useState} from 'react'
import {useNavigate} from 'react-router-dom'
import { data } from '../data/data'
import './AddMovie.css'

const AddMovie = () => {
    let navigate = useNavigate();

    const [name, setName] = useState('')
    const [genre, setGenre] = useState('')
    const [image, setImage] = useState('')

    const nameHandler = (event) => {
        setName(event.target.value);
    }

    const genreHandler = (event) => {
        setGenre(event.target.value);
    }

    const imageHandler = (event) => {
        setImage(event.target.value);
    }

    const addMovieHandler = (event) => {
        data.push({
            name: name,
            genre: genre,
            img: image
        })
        navigate("../", ({replace: true}));
    }



    return (
        <div className="form">
            <div className="dd-wrapper">
                <div className="dd-header">
                    <div className="dd-header-title"></div>
                </div>
                <div className="dd-list">
                    <button className="dd-list-item"></button>
                    <button className="dd-list-item"></button>
                    <button className="dd-list-item"></button>
                </div>
            </div>
            <h1 className="title">Add Cost</h1>
            <label>Location</label>
            <input onChange={nameHandler}></input>
            <label>Type</label>
            <input onChange={nameHandler}></input>
            <label>Name</label>
            <input onChange={genreHandler}></input>
            <label>Amount Spent</label>
            <input onChange={imageHandler}></input>
            <button onClick={addMovieHandler}>Add Cost</button>
        </div>
    )
}

export default AddMovie
